import api from "../../services/services";
import axios from "axios";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Container, ActionsContainer } from "./style"

export default function Usuarios() {
    const [usuarios, setUsuarios] = useState(undefined);
    const [reqInfo, setReqInfo] = useState();
    const [usuariosInfo, setUsuariosInfo] = useState();
    const [page, setPage] = useState(1);

    const [showUser, setShowUsers] = useState(false);
    const [reqSucess, setReqSucess] = useState(false);

    useEffect(() => {
        loadUsuarios();
    }, [])

    useEffect(() => {
        if (usuarios !== undefined) {
            setShowUsers(true);   
        }
    }, [usuarios])

    async function loadUsuarios(page =1) {
        const response = await api.get(`/usuarios?page=${page}`)
        
        const { docs, ...usuariosInfo } = response.data;
        
        setReqInfo(usuariosInfo)
        setUsuarios(docs, usuariosInfo, page);
    }

    function prevPage() {
        if(page === 1) return;

        setPage(page -1);
        loadUsuarios(page)
    }

    function nextPage() {        
        if(page === reqInfo.pages) return;

        setPage(page + 1);
        loadUsuarios(page)
    }

    return (
        <Container>
            {!showUser ?
                <div></div>
                :
                <>
                    {usuarios.map(usuario => (
                        <article key={usuario._id}>
                            <strong>{usuario.nome}</strong>
                            <p>{usuario.matricula}</p>
                            <p><Link to={`/usuarios/${usuario._id}`}>Acessar</Link></p>
                        </article>
                    ))}
                </>
            }
            <ActionsContainer>
                <button disabled={page===1} onClick={prevPage}>Anterior</button>
                <button disabled={page===reqInfo.pages} onClick={nextPage}>Pr√≥ximo</button>
            </ActionsContainer>
        </Container>
    )
}
