import styled from "styled-components";

export const HeaderSection = styled.header`
    width: 100%;
    height: 80px;
    background-color: #000;
    font-size: 40px;
    font-weight: bold;
    color: #ffffff;
    display: flex;
    justify-content: center;
    align-items: center;
`
